var INTERVAL_TIME = 50;
/**
 * 显示提示
 * @param obj 提示对象
 * @param speed 进入进出的速度 从0到1
 * @param showDuration 显示的时间
 */
function showTips(obj, speed, showDuration) {
    var opa = 0;
    var time;
    var b = true;
    time = setInterval(function () {
        if (opa < 1) {
            opa += speed;
            if (Cyan.navigator.isIE() && Cyan.navigator.version <= 8) {
                obj.style.filter = "";
                obj.style.cssText = "-ms-filter:\"progid:DXImageTransform.Microsoft.Alpha(Opacity=" + (opa * 100) + ")\";";
            } else {
                obj.style.opacity = opa;
                //obj.style.cssText = "filter:alpha(opacity=" + (opa * 100) + ");";
                console.log(obj.style.cssText);
            }
            if(b)
            {
                obj.style.display="";
                b = false;
            }
        }
        else {
            clearInterval(time);
            setTimeout(function () {
                var t;
                t = setInterval(function () {
                    if (opa > 0) {
                        opa -= speed;
                        if (Cyan.navigator.isIE() && Cyan.navigator.version <= 8) {
                            obj.style.cssText = "-ms-filter:\"progid:DXImageTransform.Microsoft.Alpha(Opacity=" + (opa * 100) + ")\";";
                        } else {
                            obj.style.opacity = opa;
                            //obj.style.cssText = "filter:alpha(opacity=" + (opa * 100) + ");";
                            console.log(obj.style.cssText);
                        }
                    }
                    else {
                        clearInterval(t);
                    }
                }, INTERVAL_TIME);
            }, showDuration);
        }
    }, INTERVAL_TIME);
}